import React, { Component } from "react";
import Header from "./components/Header";
import Content from "./components/Content";
import Footer from "./components/Footer";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fish: [],
      bugs: [],
      selectedHemisphere: "Northern",
      currentTime: new Date(),
    };

    this.changeHemisphere = (newHemisphere) => {
      this.setState({
        selectedHemisphere: newHemisphere,
      });
    };
  }

  getBugs = async () => {
    const response = await fetch("/critters/bugs");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  getCurrentFish = async () => {
    const hemisphere = "North";
    const month = this.state.currentTime.toLocaleString("default", {
      month: "long",
    });
    const hour = this.state.currentTime.getHours();
    const response = await fetch(`/critters/fish/${hemisphere}/${month}/${hour}`);
    const body = await response.json();
    return body;
  };

  getCurrentBugs = async () => {
    const hemisphere = "North";
    const month = this.state.currentTime.toLocaleString("default", {
      month: "long",
    });
    const hour = this.state.currentTime.getHours();
    const response = await fetch(`/critters/bugs/${hemisphere}/${month}/${hour}`);
    const body = await response.json();
    return body;
  };

  componentDidMount() {
    // this.getBugs()
    //   .then((res) => this.setState({ bugs: res }))
    //   .catch((err) => console.log(err));

    this.getCurrentFish()
      .then((res) => this.setState({ fish: res }))
      .catch((err) => console.log(err));

    this.getCurrentBugs()
      .then((res) => this.setState({ bugs: res}))
      .catch((err) => console.log(err));
  }

  render() {
    const hemispheres = ["Northern", "Southern"];

    return (
      <div className="App">
        <Header />
        <div className="App-content">
          <Content
            hemispheres={hemispheres}
            selectedHemisphere={this.state.selectedHemisphere}
            changeHemisphere={this.changeHemisphere}
            bugs={this.state.bugs}
            fish={this.state.fish}
          />
        </div>
        <Footer />
      </div>
    );
  }
}

export default App;
