import React, { Component } from "react";
import Header from "./components/Header";
import Content from "./components/Content";
import Footer from "./components/Footer";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fish: [],
      bugs: [],
      selectedHemisphere: "Northern",
      currentTime: new Date(),
    };

    this.changeHemisphere = (newHemisphere) => {
      this.setState({
        selectedHemisphere: newHemisphere,
      });
    };
  }

  getFish = async () => {
    const response = await fetch("/critters/fish");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  getBugs = async () => {
    const response = await fetch("/critters/bugs");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  getDate = async () => {
    const response = await fetch(
      `/critters/fish/current?date=${this.state.currentTime.toISOString()}`
    );
    console.log(response.json());
  };

  getCurrentFish = async () => {
    const response = await fetch(
      `/critters/fish/current?date=${this.state.currentTime.toISOString()}`
    );
    console.log(response.json());
  };

  getCurrentBugs = async () => {
    const response = await fetch(
      `/critters/bugs/current?date=${this.state.currentTime.toISOString()}`
    );
    console.log(response.json());
  };

  componentDidMount() {
    this.getFish()
      .then((res) => this.setState({ fish: res }))
      .catch((err) => console.log(err));

    this.getBugs()
      .then((res) => this.setState({ bugs: res }))
      .catch((err) => console.log(err));

    let date = new Date();
    this.setState({
      currentTime: date,
    });

    this.getDate().then((res) => console.log(res));
  }

  render() {
    const hemispheres = ["Northern", "Southern"];

    return (
      <div className="App">
        <Header />
        <div className="App-content">
          <Content
            hemispheres={hemispheres}
            selectedHemisphere={this.state.selectedHemisphere}
            changeHemisphere={this.changeHemisphere}
            bugs={this.state.bugs}
            fish={this.state.fish}
          />
        </div>
        <Footer />
      </div>
    );
  }
}

export default App;
